sys_transform_script.script:
(function runTransformScript(source, map, log, target /*undefined onStart*/ ) {

	// Add your code here
	var ot = new GlideRecord('x_981023_billing_0_order_transactions');
	ot.addQuery("billing_period",target.billing_period);
	ot.addQuery('source',target.sys_id);
	ot.query();

	if(ot.next()){
		ot.client = target.client_number;
		ot.cost = 15;
		ot.update();
	}else{
		ot.billing_period = target.billing_period;
		ot.source = target.sys_id;
		ot.client = target.client_number;
		ot.cost = 15;
		ot.insert();
	}

})(source, map, log, target);
