sys_ui_action.script:
var client = new GlideRecord('x_981023_billing_0_client');
client.query();

while (client.next()) {
    var bill = new GlideRecord('x_981023_billing_0_bills');
    bill.addQuery('billing_period', current.sys_id);
    bill.addQuery('client', client.sys_id);
    bill.query();

    if (bill.next()) {
        //
    } else {
        bill.billing_period = current.sys_id;
        bill.client = client.sys_id;
        bill.insert();
    }
    var ol = new GlideRecord('x_981023_billing_0_order_lines');
    ol.addQuery('client', client.sys_id);
    ol.addQuery('billing_period', current.sys_id);

    ol.addQuery('state', 'Ready');
    ol.query();

    while (ol.next()) {
        ol.state = "Billed";
        ol.bill = bill.sys_id;
        ol.update();
    }

    var ga = new GlideAggregate('x_981023_billing_0_order_lines');
    ga.addQuery('client', client.sys_id);
    ga.addQuery('billing_period', current.sys_id);
    ga.addQuery('state', 'Billed');
    ga.addAggregate('SUM', 'cost');
    ga.query();
    var totalCost = 0.0;
    if (ga.next()) {
        totalCost = ga.getAggregate('SUM', 'cost');
    }
    gs.info('nd totalCost: ' + totalCost);
    bill.total_cost = parseFloat(totalCost);
	bill.update();
}
